mapper.select:
select ut.user_id, ut.username, ut.password, ut.email, ut.create_dt, ut.update_dt,
urt.user_role_id, urt.user_id AS urt.user_id, urt.role_id AS urt.role_id,
rt.role_id, rt.role_name, rt.role_name_kor from user_tb ut join user_role_tb urt (ut.user_id = urt.user_id) role_tb rt join user_role_tb urt (rt.role_id = urt.role_id) where username =#{username}

select ut.user_id, ut.username, ut.password, ut.email, ut.create_dt, ut.update_dt,
urt.user_role_id, urt.user_id AS urt.user_id, urt.role_id AS urt.role_id,
rt.role_id, rt.role_name, rt.role_name_kor from user_tb ut join user_role_tb urt (ut.user_id = urt.user_id) role_tb rt join user_role_tb urt (rt.role_id = urt.role_id) where userId = #{userId}
